import React, { Component } from 'react';
import './search-panel.css'

export default class SearchPannel extends Component {

    state={
        term:''
    }
    render() {
        return (
            <input className="form-control search-input"
                placeholder="search"
                value={this.state.term}
                onChange={this}
            />
        );

    }
}
